import React, { Component } from 'react';
import './Login.css';
import logo from './logo.png';
import check from './check.png';
import { Button } from 'react-bootstrap';

class LoginPane extends Component {
    constructor(props){
        super(props);
        this.state = {
            username: '',
            loggedIn: false,
        }
        this.handleClick = this.handleClick.bind(this);
        this.renderLoggedIn = this.renderLoggedIn.bind(this);
    }

    handleClick(){
        var username = this.props.onLoginClick();
        this.setState({ 
            loggedIn: true,
            username,
        });
    }

    renderLoggedIn(){
        return(
            <div className="pane">
                    <img className="check-icon"src={check}></img>
                    <p> {this.state.username} is signed in.</p>
            </div>
        );
    }

    renderNotLoggedIn(){
        return (
            <div className="pane">
                <img src={logo}></img>
                <Button 
                    onClick={this.handleClick} 
                    className="login-button" 
                    bsStyle="success" 
                    bsSize="large"
                >
                    Login to Spotify
                </Button>
            </div>
        );  
    }

    renderLogInProcess(){
        return(
            <div>
                <p>Authorizing...</p>
            </div>
        );
    }
    render(){
        if (this.state.loggedIn){
            return this.renderLoggedIn();
        } else if(this.props.authInProcess) {
            return this.renderLogInProcess();
        } else {
            returnthis.renderNotLoggedIn();
        }
    }
}

export default LoginPane;
