import React, { Component }  from 'react';
import NewWindow from 'react-new-window';

class PopupWindow extends Component {
    constructor(props){
        super(props)
        this.formAuthQuery = this.formAuthQuery.bind(this);
        this.formQueryFromParams = this.formQueryFromParams.bind(this);
    }

    componentDidMount(){
        var targetWindow = window.parent;
        console.log(targetWindow);
        targetWindow.postMessage('hello from popup', '*');
    }

    formQueryFromParams(params, delimiter = '&') {
        const keys = Object.keys(params);

        return keys.reduce((str, key, index) => {
          let query = `${str}${key}=${params[key]}`;

          if (index < (keys.length - 1)) {
            query += delimiter;
          }

          return query;
        }, '');
    }

    formAuthQuery(){
        var query = this.formQueryFromParams({
            client_id: 'b69a89e58df54733aa251e2bb81537fb',
            scope: 'user-read-library',
            redirect_uri: 'https://localhost:3000/callback',
            response_type: 'token'
        });
        return query;
    }

    formUrl(){
        return 'https://accounts.spotify.com/authorize?' + this.formAuthQuery();
    }

    render(){
        return(
            <NewWindow
              url={'https://accounts.spotify.com/authorize?${query}'}
            >
                test
            </NewWindow>
        );
    }
}

export default PopupWindow;
