import React, { Component } from 'react';
import './Login.css';
import logo from './logo.png';
import check from './check.png';
import { Button } from 'react-bootstrap';

class LoginPane extends Component {

    constructor(props){
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.renderLoggedIn = this.renderLoggedIn.bind(this);
        this.renderNotLoggedIn = this.renderNotLoggedIn.bind(this);
        this.renderLogInProcess = this.renderLogInProcess.bind(this);
    }

    handleClick(){
        this.props.onLoginClick(this.props.id);
    }

    renderLoggedIn(){
        return(
            <div className="pane">
                    <img alt='' className="check-icon"src={check}></img>
                    <p> User {this.props.id} is signed in.</p>
            </div>
        );
    }

    renderNotLoggedIn(){
        return (
            <div className="pane">
                <img alt='' src={logo}></img>
                <Button 
                    onClick={this.handleClick} 
                    className="login-button" 
                    bsStyle="success" 
                    bsSize="large"
                >
                    Login to Spotify
                </Button>
            </div>
        );  
    }

    renderLogInProcess(){
        return(
            <div className="pane">
                <p>Authorizing...</p>
            </div>
        );
    }

    render(){
        if (this.props.loggedIn){
            return this.renderLoggedIn();
        } else if(this.state.authInProcess) {
            return this.renderLogInProcess();
        } else {
            return this.renderNotLoggedIn();
        }
    }
}

export default LoginPane;
