import React, { Component }  from 'react';
import NewWindow from 'react-new-window';

class PopupWindow extends Component {
    componentDidMount(){
        var targetWindow = window.parent;
        console.log('target window', targetWindow);
        var url_string = window.location.href;
        console.log(url_string);
        var url = new URL(url_string);
        var access_token = url.searchParams.get("access_token");
        console.log('access token:', access_token);
        targetWindow.postMessage(access_token, '*');
    }

    render(){
        return(
            <NewWindow
              url={'https://accounts.spotify.com/en/authorize?client_id=b69a89e58df54733aa251e2bb81537fb&redirect_uri=http:%2F%2Flocalhost:3000%2Fcallback&scope=user-read-private%20user-read-email&response_type=token&state=123'}
            >
            </NewWindow>
        );
    }
}

export default PopupWindow;
