import React, { Component } from 'react';
import LoginPage from './LoginPage/LoginPage';
import ResultsPage from './ResultsPage/ResultsPage';
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      resultsComputed: false,
      accessToken1: '',
      accessToken2: '',
    }
  }

  //call to get Access Token
  getAccessToken() {
  //return username
      console.log('called getAccessToken');
      return 'success';
  };

  getUser(){
      var response = getAccessToken();
      if (response === 'success'){
        return 'melissamasia';
      } else {
        return 'error gathering user';
      }
  }

  //call to compare libs
  fetchComparisonData() {
    return "fetch comparison data called";
  };

  renderLoginPage(){ 
    return (
      <LoginPage 
        onLoginClick={this.getUser}
        onSubmitClick={this.fetchComparisonData}
        bothLoggedIn={(this.state.accessToken1 !== '' && this.state.accessToken2 !== '')}
      >
      </LoginPage>
    );
  }

  renderResultsPage(){
    return (
      <ResultsPage></ResultsPage>
    );
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          {!this.state.resultsComputed ? this.renderLoginPage() : this.renderResultsPage()}
        </header>
      </div>
    );
  }
}

export default App;
