        }
    }

    render(){
        if (this.state.loggedIn){
            return (
                <div className="pane">
                    <img className="check-icon"src={check}></img>
                    <p> {this.state.username} is signed in.</p>
                </div>
            );
        } else {
            return (
                <div className="pane">
                    <img src={logo}></img>
                    <Button 
                        onClick={this.handleClick} 
                        className="login-button" 
                        bsStyle="success" 
                        bsSize="large"
                    >
                        Login to Spotify
                    </Button>
                    {this.renderPopUp()}
                </div>
            );
        }
    }
}

export default LoginPane;
